pragma solidity ^0.4.18;


import './ERC20.sol';
import './SafeMath.sol';


/**
 * Standard ERC20 token
 *
 * https://github.com/ethereum/EIPs/issues/20
 * Based on code by FirstBlood:
 * https://github.com/Firstbloodio/token/blob/master/smart_contract/FirstBloodToken.sol
 */
contract StandardToken is ERC20 {
  using SafeMath for uint256; //invoking safemath library

  /**
   * @dev Fix for the ERC20 short address attack.
   */
  modifier onlyPayloadSize(uint size) {
     require(msg.data.length >= size + 4);
     _;
  }

  //To get the nonce web3.eth.getTransactionCount(eth.accounts[0])

  mapping(address => uint) balances;
  mapping(address => uint) nonceLock;
  mapping(address => uint) nonceRelease;
  mapping(address => uint) nonceTokenPay;
  mapping(address => uint) nonceDirectTransfer;
  mapping (address => mapping (address => uint)) allowed;
  mapping(address => uint256) public lockedTokenDeposits;

  function getLockedTokenDeposits(address addr) public constant returns(uint256) {
        return lockedTokenDeposits[addr];
  }

  function transfer(address _to, uint _value) onlyPayloadSize(2 * 32) public returns (bool success){
    require(_to != address(0));
    require(_value <= balances[msg.sender]);
    balances[msg.sender] = balances[msg.sender].safeSub(_value);
    balances[_to] = balances[_to].safeAdd(_value);
    Transfer(msg.sender, _to, _value);
    return true;
  }

  function transferFrom(address _from, address _to, uint _value) onlyPayloadSize(3 * 32) public returns (bool success) {
    require(_from != address(0));
    require(_to != address(0));
    var _allowance = allowed[_from][msg.sender];

    // Check is not needed because safeSub(_allowance, _value) will already throw if this condition is not met
    // if (_value > _allowance) throw;

    balances[_to] = balances[_to].safeAdd(_value);
    balances[_from] = balances[_from].safeSub(_value);
    allowed[_from][msg.sender] = _allowance.safeSub(_value);
    Transfer(_from, _to, _value);
    return true;
  }

  function balanceOf(address _owner) public constant returns (uint balance) {
    return balances[_owner];
  }

  function approve(address _spender, uint _value) public returns (bool success) {
    require(_spender != address(0));
    require((_value == 0) || (allowed[msg.sender][_spender] == 0));
    allowed[msg.sender][_spender] = _value;
    Approval(msg.sender, _spender, _value);
    return true;
  }

  function allowance(address _owner, address _spender) public constant returns (uint remaining) {
    require(_owner != address(0));
    require(_spender != address(0));
    return allowed[_owner][_spender];
  }

  //To increment allowed value is better to use this function to avoid 2 calls (and wait until the first transaction is mined)
  //_addedValue The amount of tokens to increase the allowance by.
  function increaseApproval(address _spender, uint _addedValue) public returns (bool) {
    require(_spender != address(0));
    allowed[msg.sender][_spender] = allowed[msg.sender][_spender].safeAdd(_addedValue);
    Approval(msg.sender, _spender, allowed[msg.sender][_spender]);
    return true;
  } 

  //To decrement allowed value is better to use this function to avoid 2 calls (and wait until the first transaction is mined)
  //_subtractedValue The amount of tokens to decrease the allowance by
  function decreaseApproval(address _spender, uint _subtractedValue) public returns (bool) {
    require(_spender != address(0));
    uint oldValue = allowed[msg.sender][_spender];
    if (_subtractedValue > oldValue) {
      allowed[msg.sender][_spender] = 0;
    } else {
      allowed[msg.sender][_spender] = oldValue.safeSub(_subtractedValue);
    }
    Approval(msg.sender, _spender, allowed[msg.sender][_spender]);
    return true;
  }

  function lockTokensHash(
      address _delegate,  
      address _token, 
      uint256 _nonce,     
      address _from, 
      address _to,
      uint256 _value, 
      uint256 _fee
      ) public pure returns (bytes32)
    {
        return keccak256(_delegate, _token, _nonce, _from, _to, _value, _fee);
    }

  //To lock the tokens of Guest Beeper when the Beep user accepts the service.
  function lockTokensTransfer(
      address _token, 
      uint256 _nonce,  
      address _from, 
      address _to,
      uint256 _value, 
      uint256 _fee, 
      uint8 _v, 
      bytes32 _r, 
      bytes32 _s) public returns (bool) {

      require(_from != address(0));
      require(_to != address(0));
      require(_nonce > nonceLock[_from]);

      address delegate = msg.sender;
      bytes32 delegatedTxnHash = keccak256(delegate, _token, _nonce, _from, _to, _value, _fee);
      address signatory = ecrecover(delegatedTxnHash, _v, _r, _s);
      require(signatory == _from);

      //percentage to actual fee in tokens.
      _fee = _fee.safePerc(_value);
      uint256 total = _value.safeAdd(_fee);
      require(total <= balances[_from]);

      balances[_from] = balances[_from].safeSub(total);
      lockedTokenDeposits[_from] = lockedTokenDeposits[_from].safeAdd(total);
      nonceLock[_from] = _nonce;         

      LockedTokens(_from, total, _value, _fee);
      return true;
  }

  function releaseTokensHash(
      address _delegate,  
      address _token, 
      uint256 _nonce,     
      address _from,
      address _to,  
      uint256 _value, 
      uint256 _fee) public pure returns (bytes32)
    {
        return keccak256(_delegate, _token, _nonce, _from, _to, _value, _fee);
    }

  //To release the Guest Beeper's tokens when he/Beep user cancels the job.
  function releaseTokensTransfer(
      address _token, 
      uint256 _nonce,  
      address _from, 
      address _to, 
      uint256 _value, 
      uint256 _fee, 
      uint8 _v, 
      bytes32 _r, 
      bytes32 _s) public returns (bool) {

      require(_from != address(0));
      require(_to != address(0));
      require(_nonce > nonceRelease[_from]);

      address delegate = msg.sender;
      bytes32 delegatedTxnHash = keccak256(delegate, _token, _nonce, _from, _to, _value, _fee);
      address signatory = ecrecover(delegatedTxnHash, _v, _r, _s);
      require(signatory == _from);

      //percentage to actual fee in tokens.
      _fee = _fee.safePerc(_value);
      uint256 total = _value.safeAdd(_fee);
      require(total <= lockedTokenDeposits[_from]);

      lockedTokenDeposits[_from] = lockedTokenDeposits[_from].safeSub(total);
      balances[delegate] = balances[delegate].safeAdd(_fee);
      balances[_from] = balances[_from].safeAdd(_value);
      nonceRelease[_from] = _nonce;   

      ReleasedTokens(_from, total, _value, _fee);
      return true;
  }

  function tokensToPayHash(
      address _delegate,  
      address _token, 
      uint256 _nonce,     
      address _from, 
      address _to, 
      uint256 _value, 
      uint256 _fee
      ) public pure returns (bytes32)
    {
        return keccak256(_delegate, _token, _nonce, _from, _to, _value, _fee);
    }

  //To pay for the copleted service, Beep Guest pays tokens to the Beep user.  
  function tokensToPayTransfer(
      address _token,
      uint256 _nonce,   
      address _from, 
      address _to, 
      uint256 _value, 
      uint256 _fee, 
      uint8 _v, 
      bytes32 _r, 
      bytes32 _s) public returns (bool) {

      require(_from != address(0));
      require(_to != address(0));
      require(_nonce > nonceTokenPay[_from]);

      address delegate = msg.sender;
      bytes32 delegatedTxnHash = keccak256(delegate, _token, _nonce, _from, _to, _value, _fee);
      address signatory = ecrecover(delegatedTxnHash, _v, _r, _s);
      require(signatory == _from);

      //percentage to actual fee in tokens.
      _fee = _fee.safePerc(_value);
      uint256 total = _value.safeAdd(_fee);
      require(total <= lockedTokenDeposits[_from]);

      lockedTokenDeposits[_from] = lockedTokenDeposits[_from].safeSub(total);
      balances[delegate] = balances[delegate].safeAdd(_fee);
      balances[_to] = balances[_to].safeAdd(_value);
      nonceTokenPay[_from] = _nonce;   

      BeepPaymentTransfer(_from, _to, total, _value, _fee);
      return true;
  }

  function directU2UTokensHash(
      address _delegate,  
      address _token, 
      uint256 _nonce,     
      address _from, 
      address _to, 
      uint256 _value, 
      uint256 _fee
      ) public pure returns (bytes32)
    {
        return keccak256(_delegate, _token, _nonce, _from, _to, _value, _fee);
    }

  //To transfer Beep tokens from one user to another.
  function directU2UTokenTransfer(
      address _token,
      uint256 _nonce,   
      address _from, 
      address _to, 
      uint256 _value, 
      uint256 _fee, 
      uint8 _v, 
      bytes32 _r, 
      bytes32 _s) public returns (bool) {

      require(_from != address(0));
      require(_to != address(0));
      require(_nonce > nonceDirectTransfer[_from]);

      address delegate = msg.sender;
      bytes32 delegatedTxnHash = keccak256(delegate, _token, _nonce, _from, _to, _value, _fee);
      address signatory = ecrecover(delegatedTxnHash, _v, _r, _s);
      require(signatory == _from);

      //percentage to actual fee in tokens.
      _fee = _fee.safePerc(_value);
      uint256 total = _value.safeAdd(_fee);
      require(total <= balances[_from]);

      balances[_from] = balances[_from].safeSub(total);
      balances[delegate] = balances[delegate].safeAdd(_fee);
      balances[_to] = balances[_to].safeAdd(_value);
      nonceDirectTransfer[_from] = _nonce;   

      U2UTokenTransfer(_from, _to, total, _value, _fee);
      return true;
  }   
}
